
public class Rectangle {

	double rightX;
	double leftX;
	double height;

	public Rectangle(double lX, double rX, double h) {
		this.leftX = lX;
		this.rightX = rX;
		this.height = h;
	}

	public double getHeight() {
		// TODO Auto-generated method stub
		return this.height;
	}

	public double getLeftX() {
		// TODO Auto-generated method stub
		return this.leftX;
	}

	public double getRightX() {
		// TODO Auto-generated method stub
		return this.rightX;
	}

}
